package java;

public class sum {
	
	int add(int a,int b)
	{
		int res=a+b;
		return res;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method 
        sum ob=new sum();
        System.out.println(ob.add(3, 4));
        int x=ob.add(3, 4);
        System.out.println(x);
        
       
	}

}
